def split_data(data, split = 0.2, seed = 42):
    '''Splits a SQL.DataFrame into independent training and test datasets'''

    return data.randomSplit([1-split, split], seed)

def get_training_data(df, regime, split = 0.2, seed = 42,):
    '''returns tuple of DataFrames of training and test data 
       for either High SNR or Low SNR regimes.'''
    if 'high' in regime:
        df = return_high_snr(df)

    elif 'low' in regime:
        df = return_low_snr(df)

    else: raise NameError('regime is either "high" or "low"')
    return split_data(df, split, seed)

